import * as Tone from 'tone'
import React, { Component } from 'react'

import SC_Button from './SC_Button'
import SC_ToggleButtonSet from './SC_ToggleButtonSet'
import SC_Slider from './SC_Slider'

let synth
let chorus
let pingPongDelay
// let chebyshevNode
let distortionNode
// let feedbackDelayNode
// let freeverbNode
// let frequencyShifterNode
// let jcReverbNode

export default class Container extends Component {
  constructor(props) {
    super(props)

    const synthSettings = {
      volume: -20,
      detune: 0,
      portamento: 0.05,
      envelope: {
        attack: 0.05,
        attackCurve: 'exponential',
        decay: 0.2,
        decayCurve: 'exponential',
        sustain: 0.2,
        release: 1.5,
        releaseCurve: 'exponential'
      },
      oscillator: {
        type: 'sawtooth',
        modulationType: 'sine',
        // partialCount: 0,
        // partials: [],
        phase: 0,
        harmonicity: 0.5
      }
    }

    const chorusSettings = {
      wet: 0.3,
      type: 'sine',
      frequency: 1.5,
      delayTime: 3.5,
      depth: 0.7,
      spread: 180
    }

    const pingPongDelaySettings = { wet: 0.2, delayTime: 0.25, maxDelayTime: 1 }

    // const chebyshevSettings = {
    //   wet: 0,
    //   order: 50,
    //   oversample: 'none'
    // }
    const distortionSettings = {
      wet: 0,
      distortion: 0,
      oversample: '4x'
    }
    // const feedbackDelaySettings = {
    //   wet: 0,
    //   delayTime: 0.8,
    //   maxDelay: 0.8
    // }
    //
    // const freeverbSettings = {
    //   wet: 0.9,
    //   roomSize: 0.08,
    //   dampening: 40
    // }
    //
    // const frequencyShifterSettings = {
    //   wet: 0,
    //   frequency: 42
    // }
    //
    // const jcReverbSettings = {
    //   wet: 0,
    //   roomSize: 0.5
    // }

    this.state = {
      synthSettings,
      chorusSettings,
      pingPongDelaySettings,
      distortionSettings
    }

  }

  handleStart = () => {
    const { synthSettings, chorusSettings, pingPongDelaySettings, chebyshevSettings, distortionSettings, feedbackDelaySettings, freeverbSettings, frequencyShifterSettings, jcReverbSettings } = this.state

    synth = new Tone.Synth(synthSettings)
    chorus = new Tone.Chorus(chorusSettings).start()
    pingPongDelay = new Tone.PingPongDelay(
      pingPongDelaySettings
    ).toDestination()
    // chebyshevNode = new Tone.Chebyshev(chebyshevSettings)
    distortionNode = new Tone.Distortion(distortionSettings)
    // feedbackDelayNode = new Tone.FeedbackDelay(feedbackDelaySettings)
    // freeverbNode = new Tone.Freeverb(freeverbSettings)
    // frequencyShifterNode = new Tone.FrequencyShifter(frequencyShifterSettings)
    // jcReverbNode = new Tone.JCReverb(jcReverbSettings)


    synth.chain(chorus, pingPongDelay, distortionNode)

    const sequence = [
      {
        time: '0:0:0',
        noteName: 'C3',
        duration: '4n',
        velocity: 1
      },
      {
        time: '0:0:2',
        noteName: 'A3',
        duration: '1n',
        velocity: 1
      },
      {
        time: '0:1:0',
        noteName: 'E3',
        duration: '4n',
        velocity: 1
      },
      {
        time: '0:2:0',
        noteName: 'G3',
        duration: '4n',
        velocity: 1
      },
      {
        time: '0:3:0',
        noteName: 'C3',
        duration: '4n',
        velocity: 1
      },
      {
        time: '0:3:1',
        noteName: 'E3',
        duration: '4n',
        velocity: 1
      },
      {
        time: '0:3:2',
        noteName: 'G3',
        duration: '4n',
        velocity: 1
      },
      {
        time: '1:0:0',
        noteName: 'D3',
        duration: '4n',
        velocity: 1
      },
      {
        time: '1:1:0',
        noteName: 'G3',
        duration: '4n',
        velocity: 1
      },
      {
        time: '1:1:2',
        noteName: 'E4',
        duration: '4n',
        velocity: 0.7
      },
      {
        time: '1:1:3',
        noteName: 'D4',
        duration: '4n',
        velocity: 0.8
      },
      {
        time: '1:2:0',
        noteName: 'C3',
        duration: '4n',
        velocity: 1
      },
      {
        time: '1:3:0',
        noteName: 'G3',
        duration: '4n',
        velocity: 1
      },
      {
        time: '1:3:2',
        noteName: 'C4',
        duration: '4n',
        velocity: 1
      }
    ]

    const part = new Tone.Part((time, note) => {
      synth.triggerAttackRelease(
        note.noteName,
        note.duration,
        time,
        note.velocity
      )
    }, sequence).start(0)

    part.loopEnd = '2m'
    part.loop = true
    Tone.Transport.start()
  }

  handleValueChange = (property, value) => {
    const {synthSettings, chorusSettings, pingPongDelaySettings, distortionSettings} = this.state

    if (property === 'synthType') {
      synth.oscillator.type = value
      synthSettings.oscillator.type = value
    } else if (property === 'pingPongDelayWet') {
      pingPongDelay.wet.value = value
      pingPongDelaySettings.wet = value
    }  else if (property === 'chorusWet') {
      chorus.wet.value = value
      chorusSettings.wet = value
    }  else if (property === 'distortionWet') {
      distortion.wet.value = value
      distortionSettings.wet = value
    }
    // else if (property === 'chebyshevWet') {
    //   chebyshev.wet.value = value
    //   chebyshevSettings.wet = value
    // }

    // else if (property === 'feedbackDelayWet') {
    //   feedbackDelay.wet.value = value
    //   feedbackDelaySettings.wet = value
    // } else if (property === 'freeverbWet') {
    //   freeverb.wet.value = value
    //   freeverbSettings.wet = value
    // } else if (property === 'frequencyShifterWet') {
    //   frequencyShifter.wet.value = value
    //   frequencyShifterSettings.wet = value
    // } else if (property === 'jcReverbWet') {
    //   jcReverb.wet.value = value
    //   jcReverbSettings.wet = value
    // }

    this.setState({
      synthSettings,
      chorusSettings,
      distortionSettings,
      pingPongDelaySettings
    });
  };

  render() {
    const { synthSettings, chorusSettings, pingPongDelaySettings, chebyshevSettings, distortionSettings, feedbackDelaySettings, freeverbSettings, frequencyShifterSettings, jcReverbSettings } = this.state

    const options = ['sine', 'square', 'sawtooth', 'triangle']

    return (
      <div className="Container">
        <SC_Button
          text="Art Design & Coding Community"
          handleClick={this.handleStart}
        />

        <SC_ToggleButtonSet
          name="Type"
          options={options}
          value={synthSettings.oscillator.type}
          property="synthType"
          handleChange={this.handleValueChange}
        />

        <SC_Slider
          name="Delay Wet"
          min={0}
          max={1}
          step={0.01}
          value={pingPongDelaySettings.wet}
          property="pingPongDelayWet"
          handleChange={this.handleValueChange}
        />

        <SC_Slider
          name="Chorus Wet"
          min={0}
          max={1}
          step={0.01}
          value={chorusSettings.wet}
          property="chorusWet"
          handleChange={this.handleValueChange}
        />

        <SC_Slider
          name="Distortion Wet"
          min={0}
          max={10}
          step={0.01}
          value={distortionSettings.wet}
          property="distortionWet"
          handleChange={this.handleValueChange}
        />
      </div>
    )
  }
}
