import React, { Component } from "react";

import SC_Slider from "../components/SC_Slider.jsx";
import SC_ToggleButton from '../components/SC_ToggleButton.jsx'


export default class PingPongDelayEffect extends Component {
  constructor(props) {
    super(props);
  }

  handleValueChange = (property, value) => {
    const { instrumentName, handleValueChange } = this.props;
    handleValueChange(instrumentName, property, value);
  };



  renderPingPongControls = () => {
    const { title, instrumentName, settings } = this.props;

    // const { settings, handleValueChange } = this.props

    return (
      <div className='envelopeSliders'>
        <SC_Slider
          name="Wet"
          property="pingPongDelayWet"
          min={0}
          max={1}
          step={0.01}
          value={wet}
          handleChange={this.handleValueChange}
        />

        <SC_Slider
          name="Delay Time"
          property="pingPongDelayDelayTime"
          min={0}
          max={1}
          step={0.01}
          value={delayTime}
          handleChange={this.handleValueChange}
        />

        <SC_Slider
          name="Max Delay Time"
          property="pingPongDelayMaxDelayTime"
          min={0}
          max={1}
          step={0.01}
          value={maxDelayTime}
          handleChange={this.handleValueChange}
        />

      </div>
    )
  }











  render() {
    const { title, instrumentName, settings } = this.props;
    const { wet, delayTime, maxDelayTime } = settings.pingPongDelay;

    return (
      <div className="PingPongDelayEffect">
        <h3>{title}</h3>

        <div  className='envelope'>
                <SC_ToggleButton
                  text="PingPong"
                  isOn={settings.synthUI.pingPongShow}
                  handleClick={() =>
                    this.handleValueChange(
                      'synthShowPingPong',
                      !settings.synthUI.pingPongShow
                    )
                  }
                />
                </div>

      </div>
    );
  }
}
