import React, { PureComponent } from 'react'

export default class Surface extends PureComponent {
  constructor(props) {
    super(props)
  }

  render() {
    const { minX, maxX, stepX, valueXminY, maxY, stepY, valueY } = this.props

    const sizeX = 560
    const sizeY = 300

    const rangeY = maxY - minY
    const stepsY = rangeY / stepY
    const stepInPixelsY = sizeY / stapsY
    const pointerY = valueY / stepY * stepInPixelsY

    const rangeX = maxX - minX
    const stepsX = rangeX / stepX
    const stepInPixelsX = sizeX / stapsX
    const pointerX = valueX / stepX * stepInPixelsX

    return (
      <div className="Surface">
      {pointerY}
      <br/>
      {pointerX}

        <div className="pointer"></div>
      </div>
    )
  }
}





